mapper.update:
UPDATE file
SET title = #{title}
    ,writer = #{writer}
    ,content = #{content}
WHERE no = #{no}

UPDATE file
parent_table = #{parentTable}, parent_no = #{parentNo}, name = #{name}, path = #{path}, size = #{size}, content_type = #{contentType}, sort_order = #{sortOrder}, is_main = #{isMain},
WHERE id = #{id}

mapper.select:
SELECT *
FROM file
WHERE parent_table = #{parentTable}
    AND parent_no = #{parentNo}
ORDER BY sort_order ASC, created_at DESC
